import React, { Component } from 'react'
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Button,
  LayoutAnimation,
  StatusBar,
} from 'react-native'
import { auth, getCourses, getCourse, getFolder } from './api'

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  input: {
    height: 40,
    width: '60%',
    borderColor: 'gray',
    borderWidth: 1,
  },
})

export default class App extends Component {
  constructor() {
    super()
    this.state = {
      username: '',
      password: '',
      login: false,
      loginError: undefined,
      courses: [],
      folders: [],
      loading: false,
    }
  }

  go = async () => {
    try {
      // https://github.com/futuretap/FTLinearActivityIndicator for iPhone X
      this.setState({ login: false, loginError: undefined, loading: true })
      const login = await auth(this.state.username, this.state.password)
      this.setState({ login })
      const courses = await getCourses()
      LayoutAnimation.configureNext(LayoutAnimation.Presets.spring)
      this.setState({ courses })
      await Promise.all(
        courses.map(async (course, i) => {
          const folders = await getCourse(course.id)
          courses[i].folders = folders
        })
      )
      LayoutAnimation.configureNext(LayoutAnimation.Presets.easeInEaseOut)
      this.setState({ courses })
      // const folders = await getFolder({ courseId: '10252', id: '63183' })
      // LayoutAnimation.configureNext(LayoutAnimation.Presets.linear)
      // this.setState({ folders, loading: false })
      this.setState({ loading: false })
    } catch (loginError) {
      this.setState({ loginError })
    }
  }

  render() {
    console.log(this.state)
    return (
      <View style={styles.container}>
        <StatusBar networkActivityIndicatorVisible={this.state.loading} />
        <TextInput
          style={styles.input}
          autoCapitalize="none"
          onChangeText={username => this.setState({ username })}
        />
        <TextInput
          style={styles.input}
          secureTextEntry
          onChangeText={password => this.setState({ password })}
        />
        <Button title="Login get courses" onPress={this.go} />
        <Text>{`Login: ${this.state.login}`}</Text>
        {this.state.loginError && (
          <Text>{`Error: ${this.state.loginError.message}`}</Text>
        )}
        {this.state.courses.map(course => (
          <View key={course.id}>
            <Text>{`${course.acronym} s${course.section} ${course.name}`}</Text>
            <Text>Carpetas</Text>
            {Object.keys(course.folders).map(id => (
              <Text key={id}>{`${course.folders[id].name}`}</Text>
            ))}
          </View>
        ))}
        {this.state.folders.map(data => (
          <Text key={data.text}>{data.text}</Text>
        ))}
      </View>
    )
  }
}

import {
  LOGIN_URL,
  CURRENT_COURSES_URL,
  coursesUrl,
  courseUrl,
  folderUrl,
} from './urls'
import { parseCourses, parseCourse, parseFolder } from './parser'
import wait from './wait'

let SIDING_USER = ''
let SIDING_PASSWORD = ''

export function request(url, options = {}, repeat = 1) {
  const req = new XMLHttpRequest()
  req.open(options.method || 'GET', url)
  req.withCredentials = true
  const promise = new Promise(res => {
    req.onload = function handleResponse() {
      if (req.status === 500 && repeat < 5) {
        wait(repeat)
        res(request(url, options, repeat + 1))
      }
      res(req.response)
    }
  })
  req.send(options.body)
  return promise
}

export function get(url, options) {
  return request(url, { ...options, method: 'GET' })
}

export function post(url, options = {}) {
  return request(url, { ...options, method: 'POST' })
}

export function authorizedGet(url, options) {
  return auth(SIDING_USER, SIDING_PASSWORD).then(r => get(url, options))
}

export function authorizedPost(url, options = {}) {
  return auth(SIDING_USER, SIDING_PASSWORD).then(r => post(url, options))
}

export function formData(json) {
  const form = new FormData()
  Object.keys(json).forEach(k => form.append(k, json[k]))
  return form
}

export async function auth(login, passwd) {
  const body = formData({ login, passwd, sw: '', sh: '', cd: '' })
  const html = await post(LOGIN_URL, { body })
  if (html.indexOf('passwd') >= 0) throw new Error('Siding auth failed')
  SIDING_USER = login
  SIDING_PASSWORD = passwd
  return Promise.resolve(true)
}

export async function getCourses({ semester, year } = {}) {
  let url = CURRENT_COURSES_URL
  if (semester && year) url = coursesUrl(semester, year)
  const html = await authorizedGet(url)
  return parseCourses(html)
}

export async function getCourse(id) {
  if (!id) throw new Error('Course id required for getCourse')
  const html = await authorizedGet(courseUrl(id))
  return parseCourse(html)
}

export async function getFolder({ id, courseId }) {
  if (!id || !courseId)
    throw new Error('Folder and course id required for getFolder')
  const html = await authorizedGet(folderUrl({ id, courseId }))
  return parseFolder(html)
}

import { DOMParser } from 'react-native-html-parser'

const parser = new DOMParser({
  errorHandler: {
    warning: () => {},
  },
})

/**
 *  Parse a course html to find its courses
 *  @author @negebauer
 *  @param  {string} html The full html of the main course page
 *  @return {Array}       An array with course objects
 */
export function parseCourses(html) {
  return parser
    .parseFromString(html)
    .querySelect('a')
    .filter(anchor => anchor.getAttribute('href').indexOf('id_curso_ic') >= 0)
    .map(anchor => {
      const id = anchor.getAttribute('href').split('id_curso_ic=')[1]
      const text = anchor.textContent
      const split = text.split(/ s\.[0-9] /)
      const acronym = split[0]
      const name = split[1]
      const section = text.split(' ')[1].match(/\d+/)[0]
      return { id, acronym, name, section, folders: {} }
    })
}

export function parseCourse(html) {
  return parser
    .parseFromString(html)
    .querySelect('a')
    .filter(anchor => anchor.getAttribute('href').indexOf('acc_carp') >= 0)
    .map(anchor => {
      const id = anchor
        .getAttribute('href')
        .match(/id_carpeta=\d+/g)[0]
        .split('=')[1]
      const name = anchor.textContent
      return { id, name }
    })
}

export function parseFolder(html) {
  const doc = parser.parseFromString(html)
  const links = doc
    .querySelect('a')
    .filter(anchor => anchor.getAttribute('href').indexOf('id_archivo') >= 0)
    .map(anchor => ({
      href: anchor.getAttribute('href'),
      text: anchor.textContent,
    }))
  return links
}

import { SIDING_YEAR, SIDING_SEMESTER } from '../utils/date'

export const BASE_URL = 'https://intrawww.ing.puc.cl/siding'
export const COURSES_URL = `${BASE_URL}/dirdes/ingcursos/cursos`
export const LOGIN_URL = `${BASE_URL}/index.phtml`

export function coursesUrl({ semester, year }) {
  return `${COURSES_URL}/index.phtml?per_lista_cursos=${semester}_${year}&acc_inicio=mis_cursos`
}

export function courseUrl(id) {
  return `${COURSES_URL}/vista.phtml?accion_curso=avisos&acc_aviso=mostrar&id_curso_ic=${id}`
}

export function coursesCatalogUrl({ semester, year }) {
  return `${COURSES_URL}/index.phtml?acc_inicio=catalogo&per_lista_cursos=2${semester}_${year}`
}

export function folderUrl({ id, courseId }) {
  return `${COURSES_URL}/vista.phtml?accion_curso=carpetas&acc_carp=abrir_carpeta&id_curso_ic=${courseId}&id_carpeta=${id}`
}

export const CURRENT_COURSES_URL = coursesUrl({
  semester: SIDING_SEMESTER,
  year: SIDING_YEAR,
})

