import keyMirror from 'keymirror'
import pathToRegexp from 'path-to-regexp'
import queryString from 'query-string'

const ROUTES = {
  initialScreen: '/',
  currentCourses: '/currentCourses',
  course: '/course/:id',
}

const compiledRoutePaths = {}

export const routeNames = keyMirror(ROUTES)

export function buildUrl(path, params, search) {
  compiledRoutePaths[path] =
    compiledRoutePaths[path] || pathToRegexp.compile(path)
  const searchString = search ? `?${queryString.stringify(search)}` : ''
  return `${compiledRoutePaths[path](params)}${searchString}`
}

export function routeNameToPath(routeName) {
  return ROUTES[routeName]
}

export function routeNameToLocation(routeName, params, search) {
  return buildUrl(routeNameToPath(routeName), params, search)
}

import {
  LOGIN_URL,
  LOGOUT_URL,
  CURRENT_COURSES_URL,
  coursesUrl,
  courseUrl,
  folderUrl,
} from './urls'
import { SIDING_SEMESTER, SIDING_YEAR } from './date'
import { parseCourses, parseCourse, parseFolder } from './parser'
import wait from './wait'

/**
 *  Strings that appear on a siding login failed page
 *  @type {Array<String>}
 */
const SIDING_ERROR_MESSAGES = ['passwd', 'Datos de ingreso incorrectos']

/**
 *  Siding authentication failed error
 *  @type {Error}
 */
export const SIDING_AUTH_FAILED = new Error('Siding auth failed')

let SIDING_USERNAME = ''
let SIDING_PASSWORD = ''

/**
 *  Transforms a json into a FormData object
 *  @author @negebauer
 *  @param  {Object} json The json object to convert
 *  @return {FormData}    A FormData with the json data
 */
export function formData(json) {
  const form = new FormData()
  Object.keys(json).forEach(k => form.append(k, json[k]))
  return form
}

/**
 *  Executes a siding request
 *  @author @negebauer
 *  @param  {String} url          The url to perform the request to
 *  @param  {Object} [options={]} Additional request options
 *  @param  {Number} [repeat=1]   How many times the request has been tried
 *  @return {Promise<Any>}        A promise that resolves with the response
 */
export function request(url, options = {}, repeat = 1) {
  const req = new XMLHttpRequest()
  req.open(options.method || 'GET', url)
  req.withCredentials = true
  const promise = new Promise(res => {
    req.onload = () => {
      if (req.status === 500 && repeat < 6) {
        res(request(url, options, repeat + 1))
      }
      res(req.response)
    }
  })
  req.send(options.body)
  return wait(repeat).then(() => promise)
}

export function get(url, options) {
  return request(url, { ...options, method: 'GET' })
}

export function post(url, options = {}) {
  return request(url, { ...options, method: 'POST' })
}

export function authorizedGet(url, options) {
  return auth(SIDING_USERNAME, SIDING_PASSWORD).then(r => get(url, options))
}

export function authorizedPost(url, options = {}) {
  return auth(SIDING_USERNAME, SIDING_PASSWORD).then(r => post(url, options))
}

/**
 *  Authenticates the user with siding
 *  @author @negebauer
 *  @param  {String} login            Users' siding username
 *  @param  {String} passwd           Users' siding password
 *  @return {Promise<boolean>}        A promise that resolves to true after auth is succesfull
 */
export async function auth(login, passwd) {
  const body = formData({ login, passwd, sw: '', sh: '', cd: '' })
  const html = await post(LOGIN_URL, { body })
  if (SIDING_ERROR_MESSAGES.filter(e => html.indexOf(e) >= 0).length > 0)
    throw SIDING_AUTH_FAILED
  SIDING_USERNAME = login
  SIDING_PASSWORD = passwd
  return Promise.resolve({
    username: login,
    password: passwd,
  })
}

export function logout() {
  return get(LOGOUT_URL)
}

export async function loadCourses(semester, year) {
  if (semester && year) {
    const html = await authorizedGet(coursesUrl(semester, year))
    return parseCourses(html, semester, year)
  } else {
    const html = await authorizedGet(CURRENT_COURSES_URL)
    return parseCourses(html, SIDING_SEMESTER, SIDING_YEAR)
  }
}

export async function loadCourse(id) {
  if (!id) throw new Error('Course id required for getCourse')
  const html = await authorizedGet(courseUrl(id))
  return parseCourse(html)
}

export async function loadFolder({ id, courseId }) {
  if (!id || !courseId)
    throw new Error('Folder and course id required for getFolder')
  const html = await authorizedGet(folderUrl(id, courseId))
  return parseFolder(html)
}

/**
 *  Transforms a month into a siding semester
 *  @author @negebauer
 *  @param  {Date} date The date to obtain the month from
 *  @return {String}   The siding semester (21, 22 or 23)
 */
export function sidingSemester(date) {
  const month = date.getMonth() + 1
  if (month === 1) return '23'
  else if (month <= 7) return '21'
  return '22'
}

/**
 *  The user's current date
 *  @type {Date}
 */
export const CURRENT_DATE = new Date()

/**
 *  The current siding year
 *  @type {number}
 */
export const SIDING_YEAR = CURRENT_DATE.getFullYear()

/**
 *  The current siding semester
 *  @type {String}
 */
export const SIDING_SEMESTER = sidingSemester(CURRENT_DATE)

export function sidingDateId(semester = SIDING_SEMESTER, year = SIDING_YEAR) {
  return `${year}-${semester}`
}

export function sidingDateIdHuman(dateId) {
  const [year, semester] = dateId.split('-')
  switch (semester) {
    case '23':
      return `${year}-TAV`
    case '22':
      return `${year}-2`
    case '21':
      return `${year}-1`
  }
}

import { DOMParser } from 'react-native-html-parser'

/**
 *  The DOMParser for parsing siding responses
 *  @type {DOMParser}
 */
const parser = new DOMParser({
  errorHandler: {
    warning: () => {},
  },
})

/**
 *  Parse a courses' list page to read its courses
 *  @author @negebauer
 *  @param  {String} html     The full html of the main courses page
 *  @return {Array<Object>}   An array with course objects { id, acronym, name, section, folder: {} }
 */
export function parseCourses(html, semester, year) {
  return parser
    .parseFromString(html)
    .querySelect('a')
    .filter(anchor => anchor.getAttribute('href').indexOf('id_curso_ic') >= 0)
    .map(anchor => {
      const id = anchor.getAttribute('href').split('id_curso_ic=')[1]
      const text = anchor.textContent
      const split = text.split(/ s\.[0-9] /)
      const acronym = split[0]
      const name = split[1]
      const section = text.split(' ')[1].match(/\d+/)[0]
      return { id, acronym, name, section, semester, year, folders: {} }
    })
}

/**
 *  Parses a siding course's page to read it's folders
 *  @author @negebauer
 *  @param  {String} html        The siding course's page
 *  @return {Array<Object>}      An array of siding courses { id, name }
 */
export function parseCourse(html) {
  return parser
    .parseFromString(html)
    .querySelect('a')
    .filter(anchor => anchor.getAttribute('href').indexOf('acc_carp') >= 0)
    .map(anchor => {
      const id = anchor
        .getAttribute('href')
        .match(/id_carpeta=\d+/g)[0]
        .split('=')[1]
      const name = anchor.textContent
      return { id, name }
    })
}

export function parseFolder(html) {
  const doc = parser.parseFromString(html)
  const links = doc
    .querySelect('a')
    .filter(anchor => anchor.getAttribute('href').indexOf('id_archivo') >= 0)
    .map(anchor => ({
      href: anchor.getAttribute('href'),
      text: anchor.textContent,
    }))
  return links
}

